    <script type="text/javascript">
        var err_request = '数据上传时发生一个错误';

        function showFaqText(id) {
            var show = $('#faq_text'+id).css('display') == 'none' ? true : false;
            //$('.fl-answer').hide();

            if (show) {
                $('#faq_text'+id).show();
            } else {
                $('#faq_text'+id).hide();
            }
        }

        var faq_items_state = false;
        function expandAll(){
            $('#faq_items_toggler').find('span').hide()
            if (faq_items_state == false){
                $('.fl-answer').show();
                faq_items_state = true;
                $('#faq_items_toggler').find('span:eq(1)').css('display', 'inline-block');
                $('#faq_items_toggler').find('span:eq(1)').removeClass('hide');
            } else {
                $('.fl-answer').hide();
                faq_items_state = false;
                $('#faq_items_toggler').find('span:eq(0)').css('display', 'inline-block');
                $('#faq_items_toggler').find('span:eq(0)').removeClass('hide');
            }
        }

        function form_validate()
        {
            var selector = '#form_faq';
            var form = $(selector);
            var validate_path = '/faq/validate/';

            JsHttpRequest.query(validate_path, form.serialize(),
                    function(result, errors){
                        if (!result){
                            $.dqMessage(err_request, {type:'error', 'life':10000});
                            if(errors) $.dqMessage(errors, {type : 'error', 'life':30000});
                            return;
                        } else if (result.res == 1){
                            $('#new_question').html(result.text);
                            return;
                        } else {
                            $.dqValidate('#form_faq', result);
                            // $.dqMessage(result.errors, {type : 'error', 'life':10000});
                        }
                    }, true
            );
        }

    </script>


